import csv
import random
import os

def prior_random(array):
    random.shuffle(array)
    return array

def ReadTxtName(rootdir):
    lines = []
    with open(rootdir, 'r') as file_to_read:
        while True:
            line = file_to_read.readline()
            if not line:
                break
            line = line.strip('\n')
            line = line.split(',')
            line.remove('')
            line = [int(i) for i in line]
            lines.append(line)
    return lines

def apfd(report_file, sequence_file):
    """
    获取apfd
    :param report_file:
    :param sequence:
    :return:
    """
    result = [[row[0] for row in csv.reader(open(report_file, 'r', encoding='UTF-8'))],
              [row[5] for row in csv.reader(open(report_file, 'r', encoding='UTF-8'))]]
    # prioritized_array = prior_random(result[0][:])
    # prioritized_array = prior_semprior(similarity_file)
    prioritized_array = ReadTxtName(sequence_file)
    apfd_list = []
    for sequence_list in prioritized_array:
        apfd_item = []
        full_category = list(set(result[1]))
        for report in sequence_list:
            real_index = result[0][report]
            category = result[1][result[0].index(real_index)]
            if category in full_category:
                apfd_item.append(sequence_list.index(report))
                full_category.remove(category)
        apfd = 1 + 1 / (2 * len(result[0])) - sum(apfd_item) / (len(set(result[1])) * len(result[0]))
        apfd_list.append(apfd)
    return apfd_list

def get_apfd(report_file, sequence):
    """
    获取apfd
    :param report_file:
    :param sequence:
    :return:
    """
    result = [[row[0] for row in csv.reader(open(report_file, 'r', encoding='UTF-8'))],
              [row[2]for row in csv.reader(open(report_file, 'r', encoding='UTF-8'))]]
    result[0] = result[0][1:]
    result[1] = result[1][1:]
    apfd_item = []
    full_category = list(set(result[1]))
    for report in sequence:
        real_index = result[0][report]
        #real_index = str(report)
        category = result[1][result[0].index(real_index)]
        if category in full_category:
            apfd_item.append(sequence.index(report))
            full_category.remove(category)
    apfd = 1 + 1 / (2 * len(result[0])) - sum(apfd_item) / (len(set(result[1])) * len(result[0]))
    return apfd

